//Get the relevant points on eye


var xval=0;
var leftEyeReadings = [];
var rightEyeReadings = [];
var leftEyeAvg =[];
var rightEyeAvg =[];
var smoothing =15;
var threshold =4;

smooth = function(arr,smooth){
  return arr.slice(arr.length-smooth,arr.length).reduce((p,c) => p+c)/smooth ;
}


distance = function(v1,v2){
  dx = v1[0]-v2[0];
  dy = v1[1]-v2[1];
  dz = v1[2]-v2[2];
  
  return Math.sqrt(Math.pow(dx,2) + Math.pow(dy,2) + Math.pow(dz,2));
  
}

// Base64 encoded string for beep sound
function beep() {
  var snd = new Audio("data:audio/wav;base64,");
  snd.play();
}


var chart = new CanvasJS.Chart("chartContainer", {
	animationEnabled: true,
	theme: "light2",
	title:{
		text: "Distance between eyebrows"
	},
	data: [{        
		type: "line",
      	indexLabelFontSize: 16,
		dataPoints: leftEyeAvg
	},
	{        
		type: "line",
      	indexLabelFontSize: 16,
		dataPoints: rightEyeAvg
	}]
});




businesslogic = function(predictions){
  
  leftEye = (distance(predictions[0]['mesh'][385],predictions[0]['mesh'][380])+distance(predictions[0]['mesh'][386],predictions[0]['mesh'][374]))/2;
  rightEye = (distance(predictions[0]['mesh'][159],predictions[0]['mesh'][145])+distance(predictions[0]['mesh'][158],predictions[0]['mesh'][153]))/2;
  
  leftEyeReadings.push(leftEye);
  rightEyeReadings.push(rightEye);
  xval++;
  
  if (leftEyeReadings.length>smoothing || rightEyeReadings.length>smoothing){
    leftSmooth = smooth(leftEyeReadings,smoothing);
    rightSmooth = smooth(rightEyeReadings,smoothing); 
    
    leftEyeAvg.push({'x':xval,'y':leftSmooth});
    rightEyeAvg.push({'x':xval,'y':rightSmooth});
    
    if (leftEyeAvg.slice(-1)[0]['y']<threshold & rightEyeAvg.slice(-1)[0]['y']<threshold){
      beep();
    }
  }
  
  
  chart.render();
}

